"use strict";var fader=document.querySelectorAll(".fader"),faderText=document.querySelectorAll(".fader-text"),slideUpContainer=document.querySelectorAll(".slideup-container"),slideupContent=document.querySelectorAll(".slideup-content"),winHeight=window.innerHeight;window.addEventListener("scroll",function(){for(var e=0;e<fader.length;e++){var t=fader[e],n=Math.floor(window.innerHeight/2);t.getBoundingClientRect().top<n?t.classList.add("show"):t.classList.remove("show")}for(var o=0;o<faderText.length;o++){var i=faderText[o],r=Math.floor(window.innerHeight/2);i.getBoundingClientRect().top<1.2*r?i.classList.add("show"):i.classList.remove("show")}slideUpContainer.forEach(function(e,t){e.getBoundingClientRect().top<winHeight/2?slideupContent[t].classList.add("show"):slideupContent[t].classList.remove("show")})});