const fader=document.querySelectorAll(".fader"),faderText=document.querySelectorAll(".fader-text"),slideUpContainer=document.querySelectorAll(".slideup-container"),slideupContent=document.querySelectorAll(".slideup-content"),winHeight=window.innerHeight;window.addEventListener("scroll",function(){for(let e=0;e<fader.length;e++){let t=fader[e],n=Math.floor(window.innerHeight/2);t.getBoundingClientRect().top<n?t.classList.add("show"):t.classList.remove("show")}for(let e=0;e<faderText.length;e++){let t=faderText[e],n=Math.floor(window.innerHeight/2);t.getBoundingClientRect().top<1.2*n?t.classList.add("show"):t.classList.remove("show")}slideUpContainer.forEach((e,t)=>{e.getBoundingClientRect().top<winHeight/2?slideupContent[t].classList.add("show"):slideupContent[t].classList.remove("show")})});